import React, { useEffect, useState } from 'react';
import DirectMessages from './DirectMessages'; 
import '../css/messages.css';

const MessagesButton = () => {
    const [isDropdownOpen, setDropdownOpen] = useState(false);
    const [messages, setMessages] = useState([]);
    const [selectedFriend, setSelectedFriend] = useState(null);

    useEffect(() => {
        fetch('/get_messages')
        .then(response => response.json())
        .then(data => setMessages(data))
        .catch(error => console.log("Error fetching messages", error))
    }, []);

    const handleFriendClick = (friend) => {
        setSelectedFriend(friend);
        setDropdownOpen(false); //closes dropdown when friend is selected
    };

    return (
        <div className="messages-container">
            <button id="messages-button" onClick={() => setDropdownOpen(!isDropdownOpen)}>Messages</button>

            {isDropdownOpen && (
                <div className="messages-dropdown">
                    {messages.map(item => (
                        <div key={item.friend_name} onClick={() => handleFriendClick(item)}>
                            <p>{item.friend_name}</p>
                        </div>
                    ))}
                </div>
            )}
            {selectedFriend && <DirectMessages friend={selectedFriend} />}
        </div>
    );
};

export default MessagesButton;
