import React, { useEffect, useState } from 'react';
import DirectMessages from './directMessages'; 
import '../css/messages.css';

const MessagesButton = () => {
    const [isDropdownOpen, setDropdownOpen] = useState(false);
    const [messages, setMessages] = useState([]);
    const [currentChat, setCurrentChat] = useState(null);

    useEffect(() => {
        fetch('/get_friends')
        .then(response => response.json())
        .then(data => setMessages(data))
        .catch(error => console.log("Error fetching friends", error))
    }, []);

    const openChat = (friendId, friendName) => {
        setCurrentChat({ friendId, friendName});
    };

    const [selectedFriend, setSelectedFriend] = useState(null);
    const [conversationId, setConversationId ] = useState(null);

    const handleFriendClick = async (friendId, conversationId) => {
        console.log("Friend clicked:", friendId, conversationId);
        onSelectFriend(friendId, conversationId);
        try{
            const response = await fetch('/start_conversation/${friendId}');
            const data = await response.json();
            if (data.error){
                console.error(data.error);
            }
            else{
                setConversationId(data.conversation_id);
                setSelectedFriend(friendId);
            }
            }
        catch (error){
                console.error("Error starting conversation", error);
        }
    };

    return (
        <div className="messages-container">
            <button id="messages-button" onClick={() => setDropdownOpen(!isDropdownOpen)}>Messages</button>

            {isDropdownOpen && (
                <div className="messages-dropdown">
                    {messages.map(item => (
                        <div className="friend-chat-box" key={item.friend_id} onClick={() => openChat(item.friend_id)}>
                            <p>{item.friend_name}</p>
                        </div>
                    ))}
                </div>
            )}
            {selectedFriend && <DirectMessages friendId={selectedFriend} conversationId={conversationId} />}
        </div>
    );
};

export default MessagesButton;
