import React, { useEffect, useState } from 'react';
import DirectMessages from './directMessages'; 
import '../css/messages.css';

const MessagesButton = () => {
    const [isDropdownOpen, setDropdownOpen] = useState(false);
    const [friends, setFriends] = useState([]);
    const [selectedFriend, setSelectedFriend] = useState(null);
    const [conversationId, setConversationId] = useState(null);
    const [chatBoxStyle, setChatBoxStyle] = useState({});

    useEffect(() => {
        fetch('/get_friends')
        .then(response => response.json())
        .then(data => setFriends(data))
        .catch(error => console.log("Error fetching friends", error))
    }, []);

    const openChat = async (friendId, friendName, conversationId, event) => {
        //Calculate the position where the chat box should appear
        const rect = event.currentTarget.getBoundingClientRect();
        setChatBoxStyle({
            position: 'absolute',
            top: `${rect.bottom + window.scrollY}px`,
            left: `${rect.left + window.scrollX}px`
        });
        setSelectedFriend(friendId);
        setConversationId(conversationId);
    };

    return (
        <div className="messages-container">
            <button id="messages-button" onClick={() => setDropdownOpen(!isDropdownOpen)}>Messages</button>

            {isDropdownOpen && (
                <div className="messages-dropdown">
                    {friends.map(friend => (
                        <div className="friend-chat-box" key={friend.friend_id} 
                        onClick={() => openChat(friend.friend_id, friend.friend_name, friend.conversation_id)}>
                            <p>{friend.friend_name}</p>
                        </div>
                    ))}
                </div>
            )}
            {selectedFriend && (
            <div style={chatBoxStyle}>
                <DirectMessages friendId={selectedFriend} conversationId={conversationId} />
            </div>
            )}
        </div>
    );
};

export default MessagesButton;
