import React from 'react';
import ReactDOM from 'react-dom';

function SendFriendRequestButton({ receiverId }) {
    const handleSendRequest = () => {
        fetch(`/send_request/${receiverId}`, { method: 'POST' })
            .then(response => response.json())
            .then(data => {
                alert(data.message);
            });
    };

    return (
        <div>
            <button onClick={handleSendRequest}>Send Friend Request</button>
        </div>
    );
}

const rootElement = document.getElementById('friend-request-root');

//Check if rootElement exists
if (rootElement){
    const receiverId = rootElement.getAttribute('data-receiver-id');
    ReactDOM.render(
        <React.StrictMode>
          <SendFriendRequestButton receiverId={receiverId} />
        </React.StrictMode>,
        rootElement
      );
}

export default SendFriendRequestButton
