import React from 'react';
import ReactDOM from 'react-dom';

function SendFriendRequestButton({ senderId, receiverId }) {
    const handleSendRequest = () => {
        console.log('Sending friend request...');
        fetch(`/send_request/${senderId}/${receiverId}`, { method: 'POST' })
            .then(response => response.json())
            .then(data => {
                alert(data.message);
            });
    };

    return (
        <div>
            <button onClick={handleSendRequest}>Send Friend Request</button>
        </div>
    );
}

const rootElement = document.getElementById('friend-request-root');
const senderId = rootElement.getAttribute('data-sender-id');
const receiverId = rootElement.getAttribute('data-receiver-id');

ReactDOM.render(
  <React.StrictMode>
    <SendFriendRequestButton senderId={senderId} receiverId={receiverId} />
  </React.StrictMode>,
  rootElement
);
