import React, { useState, useEffect } from 'react';

function FriendRequestComponent({ userId }) {
    const [requests, setRequests] = useState([]);

    useEffect(() => {
        //Fetch friend requests for user
        fetch(`/get_requests/${userId}`)
            .then(response => response.json())
            .then(data => setRequests(data));
    }, [userId]);

    const handleAccept = (requestId) => {
        fetch(`/accept_request/${requestId}`, { method: 'PUT' })
            .then(() => {
                //Update UI after accepting
                setRequests(requests.filter(req => req.id !== requestId));
            });
    };

    const handleReject = (requestId) => {
        fetch(`/reject_request/${requestId}`, { method: 'DELETE' })
            .then(() => {
                //Update UI after rejecting
                setRequests(requests.filter(req => req.id !== requestId));
            });
    };

    return (
        <div>
            {requests.map(request => (
                <div key={request.id}>
                    <p>Request from: {request.from}</p>
                    <button onClick={() => handleAccept(request.id)}>Accept</button>
                    <button onClick={() => handleReject(request.id)}>Reject</button>
                </div>
            ))}
        </div>
    );
}
